-- Table: Appointments
CREATE TABLE Appointments (
    AppointmentID SERIAL PRIMARY KEY,
    AppointmentDate DATE,
    AppointmentTime TIME,
    ReasonForVisit TEXT,
    Status VARCHAR(20),
    Prescription TEXT,
    Notes TEXT,
    CreatedAt TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

